import { observer } from 'mobx-react-lite';
import React, {useContext, useEffect, useState } from "react";
import '../PageTable.css';
import {Context} from "../index";
import PositionList from "../components/positionList";
import StaffList from "../components/staffList";
import { createPosition, getPosition } from '../http/positionAPI';
import { createStaff, getStaff } from '../http/StaffAPI';

const PageTable = observer (() => {
    const {position, staff} = useContext(Context);
    const [index, setIndex] = useState(0);

    const clickPosition = async () => {
        try {
            let data = await getPosition();
            position.setPosition(data);
            console.log(data);
            setIndex(1);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }

    const clickStaff = async () => {
        try {
            let data = await getStaff();
            staff.setStaff(data);
            console.log(data);
            setIndex(2);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }

    const clickAdd = async () => {
        try {
            var val = document.getElementById('1').value;
            let data = await createPosition(val);
            position.setPosition(data);
            console.log(data);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }


    return (
        <div className="container-fluid">
        <div className="row">
            <div className="col-md-4 col-lg-3 navbar-container bg-light">
                <nav className="navbar navbar-expand-md navbar-light">
                    <h1 className="navbar-brand">Таблицы</h1>
                    <div className="collapse navbar-collapse" id="navbar">
                        <ul className="navbar-nav">
                            <li className="nav-item">
                                <button onClick={() => clickPosition()} className="btn">Должности</button>
                            </li>
                                <li className="nav-item">
                                <button onClick={() => clickStaff()} className="btn">Персонал</button>
                            </li>
                                <li className="nav-item">
                            <button className="btn">Таблица3</button>
                                </li>
                            <li className="nav-item">
                                <button className="btn">Таблица4</button>
                            </li>
                            <li className="nav-item">
                                <button className="btn">Таблица5</button>
                            </li>
                        </ul>
                    </div>
                </nav>
            </div>
            <div className="col-md-8 col-lg-9 content-container div_color">
                {index == 0 ? <h2 className="h3 text-center mt-5">Выберете таблицу</h2> : <></>}
                {index == 1 ? <PositionList/> : <></>}
                {index == 2 ? <StaffList/> : <></>}
            </div>

            <input className="inp" id="1"></input>
            <button className="btn btn_color" onClick={() => clickAdd()}>Добавить</button>
            <button className="btn btn_color">Удалить</button>
        </div>
    </div>
    )
});

export default PageTable;
