import { observer } from 'mobx-react-lite';
import React, {useContext, useEffect, useState } from "react";
import '../PageTable.css';
import {Context} from "../index";
import PositionList from "../components/positionList";
import StaffList from "../components/staffList";
import { createPosition, getPosition, delPosition } from '../http/positionAPI';
import { createStaff, getStaff } from '../http/StaffAPI';

const PageTable = observer (() => {
    const {position, staff} = useContext(Context);
    const [index, setIndex] = useState(0);

    const clickPosition = async () => {
        try {
            let data = await getPosition();
            position.setPosition(data);
            console.log(data);
            setIndex(1);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }

    const clickStaff = async () => {
        try {
            let data = await getStaff();
            staff.setStaff(data);
            console.log(data);
            setIndex(2);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }

    const clickAdd = async () => {
        try {
            var val = document.getElementById('1').value;
            if(val.length != 0)
            {
                let data = await createPosition(val);
                data = await getPosition();
                position.setPosition(data);
                console.log(data);
            }     
        } catch (e) {
            alert(e.responce.data.message);
        }
    }

    const clickDel = async () => {
        try {
            var val = document.getElementById('1').value;
            if(val.length != 0)
            {
                let data = await delPosition(val);
                data = await getPosition();
                position.setPosition(data);
                console.log(data);
            }

        } catch (e) {
            alert(e.responce.data.message);
        }
    }


    return (
        <div className="main">
        <div className="main_row">
            <div className="menu">
                <h1 className="nav_text">Таблицы</h1>
                    <ul className="navbar-nav nav_item_m">
                        <li className="nav-item">
                            <button onClick={() => clickPosition()} className="btn">Должности</button>
                        </li>
                            <li className="nav-item">
                            <button onClick={() => clickStaff()} className="btn">Персонал</button>
                        </li>
                            <li className="nav-item">
                        <button className="btn">Таблица3</button>
                            </li>
                        <li className="nav-item">
                            <button className="btn">Таблица4</button>
                        </li>
                        <li className="nav-item">
                            <button className="btn">Таблица5</button>
                        </li>
                    </ul>
            </div>
        </div>

        <div className="main_row">
            <div>
                <div className="div_color">
                    {index == 0 ? <h2 className="h3 text-center mt-4">Выберете таблицу</h2> : <></>}
                    <div className="item mt-4">
                        {index == 1 ? <PositionList/> : <></>}
                        {index == 2 ? <StaffList/> : <></>}
                    </div>  
                </div>
            </div>

            <div className="div_inp">
                <input className="inp" id="id"></input>
                <input className="inp" id="name"></input>
                <input className="inp" id="surname"></input>
                <input className="inp" id="patronymic"></input>
                <input className="inp" id="positionId"></input>
                <button className="btn" onClick={() => clickAdd()}>Добавить</button>
                <button className="btn" onClick={() => clickDel()}>Удалить</button>
            </div>
        </div>   
    </div>
    )
});

export default PageTable;
