import { observer } from 'mobx-react-lite';
import React, {useContext, useEffect, useState } from "react";
import '../PageTable.css';
import {Context} from "../index";
import PositionList from "../components/positionList";
import StaffList from "../components/staffList";
import { createPosition, getPosition, delPosition } from '../http/positionAPI';
import { createStaff, getStaff } from '../http/StaffAPI';

const PageTable = observer (() => {
    const {position, staff} = useContext(Context);
    const [index, setIndex] = useState(0);

    const clickPosition = async () => {
        try {
            let data = await getPosition();
            position.setPosition(data);
            console.log(data);
            setIndex(1);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }

    const clickStaff = async () => {
        try {
            let data = await getStaff();
            staff.setStaff(data);
            console.log(data);
            setIndex(2);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }

    const clickAdd = async () => {
        try {
            var val = document.getElementById('1').value;
            let data = await createPosition(val);
            data = await getPosition();
            position.setPosition(data);
            console.log(data);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }

    const clickDel = async () => {
        try {
            var val = document.getElementById('1').value;
            let data = await delPosition(val);
            data = await getPosition();
            position.setPosition(data);
            console.log(data);

        } catch (e) {
            alert(e.responce.data.message);
        }
    }


    return (
        <div className="main">
        <div className="main_row">
            <div className="menu">
                <nav className="navbar navbar-expand-md navbar-light">
                </nav>
            </div>
        </div>
    </div>
    )
});

export default PageTable;
