import React from 'react';
import { Button, Container, Form } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import { NavLink } from 'react-router-dom';
import { REGISTRATION_ROUTE } from '../utils/consts';

const Auth = () => {
    return (
        <div>
            <Container className="d-flex justify-content-center align-items-center"
            style={{height: window.innerHeight - 54}}>
                <Card style={{width: 600}} className="p-5">
                    <h2 className="m-auto">Авторизация</h2>
                    <Form className="d-flex flex-column">
                        <Form.Control className="mt-2" placeholder='Ведите логин'/>
                        <Form.Control className="mt-2" placeholder='Ведите пароль'/>
                    </Form>
                    <Button className="mt-3" variant={"outline-success"}>Войти</Button>
                    <NavLink class="nav-link" to={REGISTRATION_ROUTE}>Регистрация</NavLink>
                </Card>
            </Container>
        </div>
    )
}

export default Auth;
